import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';


@Component({
  selector: 'app-registerusuario',
  templateUrl: './registerusuario.component.html',
  styleUrls: ['./registerusuario.component.css']
})
export class RegisterusuarioComponent {
  form: any = {}; // Objeto para almacenar los datos del formulario

  constructor(private http: HttpClient) { }

  registerUser() {
    this.http.post('https://localhost:3000/usuarios', this.form)
      .subscribe(
        response => {
          console.log('Usuario registrado exitosamente', response);
          // Realiza cualquier acción adicional que desees, como mostrar una notificación o redirigir al usuario.
        },
        error => {
          console.error('Error al registrar el usuario', error);
          // Maneja cualquier error que pueda ocurrir, como mostrar un mensaje de error al usuario.
        }
      );
  }
}
