<link rel="stylesheet" href="https://use.fontawesome.com/releases/v5.0.8/css/all.css">

<div class="container">

  <div class="card bg-light">

    <article class="card-body mx-auto" style="max-width: 400px;">
      <div class="mb-4">
        <a href="dashboard">Regresar</a>
      </div>
      <h4 class="card-title mt-3 text-center">Crear Cuenta Dueños de Perros</h4>

      <form [formGroup]="myForm" (ngSubmit)="onSubmit()">
        <div class="form-group input-group">
          <div class="input-group-prepend">
          </div>
          <input required type="text" id="nombres" formControlName="nombres" class="form-control" placeholder="Nombres Completos">
        </div> <!-- form-group// -->
        <div class="form-group input-group">
          <div class="input-group-prepend">
          </div>
          <input required ="email" id="correo" formControlName="correo" class="form-control" placeholder="Correo Electrónico">
        </div> <!-- form-group// -->
        <div class="form-group input-group">
          <div class="input-group-prepend">
          </div>
          <select class="custom-select" style="max-width: 120px;">
            <option selected="">+971</option>
            <option value="1">+972</option>
            <option value="2">+593</option>
            <option value="3">+701</option>
          </select>
          <input required type="text" id="telefono" formControlName="telefono" class="form-control" placeholder="Número de teléfono">
        </div> <!-- form-group// -->
        <div class="form-group input-group">
          <div class="input-group-prepend">
          </div>
          <input type="text" id="cedula" formControlName="cedula" class="form-control" placeholder="Cédula">
        </div> <!-- form-group end.// -->
        <div class="form-group input-group">
          <div class="input-group-prepend">
          </div>
          <input required type="password" id="contrasena" formControlName="contrasena" class="form-control" placeholder="Crear Contraseña">
        </div> <!-- form-group// -->
        <div class="form-group input-group">
          <div class="input-group-prepend">
          </div>
          <input required type="password" id="repetir-contrasena" class="form-control" placeholder="Repite la Contraseña">
        </div> <!-- form-group// -->
        <div class="form-group">
          <button type="submit" class="btn btn-primary btn-block">Crear Cuenta</button>
        </div> <!-- form-group// -->
        <p class="text-center">Ya tienes una cuenta? <a href="login">Inicia Sesión</a></p>
        <p class="text-center">¿Eres paseador? <a href="registerpaseador">Pulsa Aquí</a></p>
      </form>
      <div class="alert alert-success mt-3" role="alert" *ngIf="showSuccessMessage">
        Se ha creado el usuario con éxito.
      </div>
    </article>

  </div> <!-- card.// -->

</div> <!-- container end.// -->
