import { Component } from '@angular/core';

@Component({
  selector: 'app-seccion-como-funciona',
  templateUrl: './seccion-como-funciona.component.html',
  styleUrls: ['./seccion-como-funciona.component.css']
})
export class SeccionComoFuncionaComponent {

}
<!DOCTYPE html>
<html lang="en">

<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">

  <style>
    .bg-img {
      background-image: url('https://pomf2.lain.la/f/tjs1k15j.jpg'); /* Replace with your image URL */
      background-size: cover;
      background-position: center center;
      height: 92vh;
      display: flex;
      align-items: center;
      opacity: 90%;
    }

    .overlay {      
      top: 0;
      left: 0;
      right: 0;
      bottom: 0;
      background-color: rgba(1, 3, 19, 0.5);
    }

    .content {
      text-align: start;
      color: #000000;
      margin-left: 0; /* Update margin-left to 0 */
    }

    .content h1 {
      font-size: 3rem;
      font-weight: bold;
    }

    .content h2 {
      font-size: 3rem;
      font-weight: bold;
    }

    .content p {
      font-size: 1.5rem;
      margin-top: 1rem;
    }

    .content button {
      margin-top: 2rem;
    }
  </style>
</head>

<body>
  <div class="bg-img">
    <div class="overlay"></div>
    <div class="container">
      <div class="row">
        <div class="col-md-6 col-lg-6">
            <div class="content"> <!-- Update without offset classes -->
              <h2>Como Funciona</h2>
              <p>Ofrecemos paseos profesionales para perros de todas las razas y tamaños.</p>
            </div>
          </div>
      </div>
      <!--4 tarjetas en grande horizontal y 2 de 2 en pequeno RENDERIZAR LAS TARJETAS DESDE EL TS-->
     <div class="container-fluid" >
        <div class="row justify-content-md-start" >
              <div class="card col-6 m-1 "  style="opacity: 0.67;">
                <div class="card-body">
                  This is some text within a card body.
                </div>
              </div>

              <div class="card col-3 m-1 " style="opacity: 0.67;">
                <div class="card-body">
                  This is some text within a card body.
                </div>
              </div>

              <div class="card col-3 m-1 " style="opacity: 0.67;">
                <div class="card-body">
                  This is some text within a card body.
                </div>
              </div>






        </div>

        <button class="btn btn-primary mt-4" >Inicia Hoy</button>
    </div>


    </div>

  </div>
</body>

</html>
