import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-registerusuario',
  templateUrl: './registerusuario.component.html',
  styleUrls: ['./registerusuario.component.css']
})
export class RegisterusuarioComponent {
  myForm: FormGroup;

  constructor(private formBuilder: FormBuilder, private http: HttpClient) {
    this.myForm = this.formBuilder.group({
      nombres: ['', Validators.required],
      correo: ['', [Validators.required, Validators.email]],
      telefono: ['', Validators.required],
      cedula: ['', Validators.required],
      contrasena: ['', Validators.required]
    });
  }

  onSubmit() {
    if (this.myForm.invalid) {
      return;
    }

    const formData = this.myForm.value;

    this.http.post('http://localhost:3000/usuarios/', formData)
      .subscribe(
        response => {
          console.log('Post request successful', response);
          // Do something with the response if needed
        },
        error => {
          console.error('Error occurred:', error);
          // Handle the error
        }
      );
  }

}
<link href="https://maxcdn.bootstrapcdn.com/bootstrap/4.0.0/css/bootstrap.min.css" rel="stylesheet">

<form [formGroup]="myForm" (ngSubmit)="onSubmit()" class="container">
  <div class="form-group">
    <label for="nombres">Nombres</label>
    <input type="text" id="nombres" formControlName="nombres" class="form-control">
  </div>

  <div class="form-group">
    <label for="correo">Correo</label>
    <input type="email" id="correo" formControlName="correo" class="form-control">
  </div>

  <div class="form-group">
    <label for="telefono">Teléfono</label>
    <input type="text" id="telefono" formControlName="telefono" class="form-control">
  </div>

  <div class="form-group">
    <label for="cedula">Cédula</label>
    <input type="text" id="cedula" formControlName="cedula" class="form-control">
  </div>

  <div class="form-group">
    <label for="contrasena">Contraseña</label>
    <input type="password" id="contrasena" formControlName="contrasena" class="form-control">
  </div>

  <button type="submit" class="btn btn-primary">Submit</button>
</form>
