import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-registerusuario',
  templateUrl: './registerusuario.component.html',
  styleUrls: ['./registerusuario.component.css']
})
export class RegisterusuarioComponent {
  myForm: FormGroup;

  constructor(private formBuilder: FormBuilder, private http: HttpClient) {
    this.myForm = this.formBuilder.group({
      nombres: ['', Validators.required],
      correo: ['', [Validators.required, Validators.email]],
      telefono: ['', Validators.required],
      cedula: ['', Validators.required],
      contrasena: ['', Validators.required]
    });
  }

  onSubmit() {
    if (this.myForm.invalid) {
      return;
    }

    const formData = this.myForm.value;

    this.http.post('http://localhost:3000/usuarios/', formData)
      .subscribe(
        response => {
          console.log('Post request successful', response);
          // Do something with the response if needed
        },
        error => {
          console.error('Error occurred:', error);
          // Handle the error
        }
      );
  }

}
<link href="//maxcdn.bootstrapcdn.com/bootstrap/4.0.0/css/bootstrap.min.css" rel="stylesheet">
<script src="//maxcdn.bootstrapcdn.com/bootstrap/4.0.0/js/bootstrap.min.js"></script>
<script src="//cdnjs.cloudflare.com/ajax/libs/jquery/3.2.1/jquery.min.js"></script>

<div class="container">

  <div class="card bg-light">

    <article class="card-body mx-auto" style="max-width: 400px;">
      <div class="mb-4">
        <a href="dashboard">Regresar</a>
      </div>
      <h4 class="card-title mt-3 text-center">Crear Cuenta Dueños de Perros</h4>

      <form [formGroup]="myForm" (ngSubmit)="onSubmit()">
        <div class="form-group input-group">
          <div class="input-group-prepend">
          </div>
          <input type="text" id="nombres" formControlName="nombres" class="form-control" placeholder="Nombres Completos">
        </div> <!-- form-group// -->
        <div class="form-group input-group">
          <div class="input-group-prepend">
          </div>
          <input type="email" id="correo" formControlName="correo" class="form-control" placeholder="Correo Electrónico">
        </div> <!-- form-group// -->
        <div class="form-group input-group">
          <div class="input-group-prepend">
          </div>
          <select class="custom-select" style="max-width: 120px;">
            <option selected="">+971</option>
            <option value="1">+972</option>
            <option value="2">+593</option>
            <option value="3">+701</option>
          </select>
          <input type="text" id="telefono" formControlName="telefono" class="form-control" placeholder="Número de teléfono">
        </div> <!-- form-group// -->
        <div class="form-group input-group">
          <div class="input-group-prepend">
          </div>
          <input type="text" id="cedula" formControlName="cedula" class="form-control" placeholder="Cédula">
        </div> <!-- form-group end.// -->
        <div class="form-group input-group">
          <div class="input-group-prepend">
          </div>
          <input type="password" id="contrasena" formControlName="contrasena" class="form-control" placeholder="Crear Contraseña">
        </div> <!-- form-group// -->
        <div class="form-group input-group">
          <div class="input-group-prepend">
          </div>
          <input type="password" id="repetir-contrasena" class="form-control" placeholder="Repite la Contraseña">
        </div> <!-- form-group// -->
        <div class="form-group">
          <button type="submit" class="btn btn-primary">Crear Cuenta</button>
        </div> <!-- form-group// -->
      </form>

      <div class="alert alert-success mt-3" role="alert" *ngIf="showSuccessMessage">
        Se ha creado el usuario con éxito.
      </div>

    </article>

  </div> <!-- card.// -->

</div> <!-- container end.// -->
